$(document).ready(function(){$(".slider").on("init",function(){var e=$(".slider-row, .half-slider-row");e.css("opacity",1),e.css("min-height",0)}),$(".slider:not(.lazy-slider)").slick({prevArrow:"<a class='slick-prev'></a>",nextArrow:"<a class='slick-next'></a>",adaptiveHeight:!0}),$(".lazy-slider").slick({prevArrow:"<a class='slick-prev'></a>",nextArrow:"<a class='slick-next'></a>",lazyload:"progressive"}),$(".slider--video").on("beforeChange",function(e,i,a,s){var r=$(i.$slides[s]),d=$(i.$slides[a]),t=$("#"+$(this).data("description-container"));t.find(".header").fadeOut(150,function(){$(this).html(r.data("header")),$(this).fadeIn()}),t.find(".subheader").fadeOut(150,function(){$(this).html(r.data("subheader")),$(this).fadeIn()}),d.find(".video").data("video").stop()})});